import { faHome,
         faEdit,
         faHandPointer,
         faGear,
         faPerson,
         faFileLines,
         IconDefinition } from '@fortawesome/free-solid-svg-icons';
export interface NavigationItem {
    id: string;
    title: string;
    icon: IconDefinition;
    url?: string;
    active?: boolean;
    itens: SideNavItem[]
  }

export interface SideNavItem {
    id: string;
    title: string;
    icon: IconDefinition;
    url?: string;
    hasChild: boolean;
    subItens: SideNavItem[]
}

export interface SideNavSubItem {
    id: string;
    title: string;
    url?: string; 
}
  
export class NavigationMenu {

    faHome: IconDefinition = faHome;
    faEdit: IconDefinition = faEdit;
    faHandPointer: IconDefinition = faHandPointer;
    faGear: IconDefinition = faGear;
    faPerson: IconDefinition = faPerson;
    faFileLines: IconDefinition = faFileLines;

  private navigationItems: NavigationItem[] = [
    {
        id: 'nav-home',
        title: 'Home',
        url: '/home',
        icon: faHome,
        itens:[
            {
                id: 'nav-home',
                title: 'Home',
                url: '/home',
                icon: faHome,
                hasChild: true;
                subItens:[]
            },
        ]
    },
    {
        id: 'nav-cadastro',
        title: 'Cadastro',
        url: '/cadastro',
        icon: faEdit
    },
    {
        id: 'nav-acoes',
        title: 'Ações',
        url: '/acoes',
        icon: faHandPointer
    },
    {
        id: 'nav-controladora',
        title: 'Controladora',
        url: '/controladora',
        icon: faGear
    },
    {
        id: 'nav-visita',
        title: 'Visita',
        url: '/visita',
        icon: faPerson
    },
    {
        id: 'nav-relatorios',
        title: 'Relatórios',
        active: false,
        url: '/relatorios',
        icon: faFileLines
    }
    ];

    constructor(){

    }  

    getNavigationMenu(): NavigationItem[]{
        return this.navigationItems;
    }
    
}
