    public totalPages: number;
    public itemsPerPage: number;
    public totalItems: number;
    public page: T[] = [];
    public pageButtons:(number | string)[] = [];
    private items: T[];

    constructor(itemsPerPage:number, items:T[]){
        this.items = items;
        this.itemsPerPage = itemsPerPage;
        this.setTotamItems();
        this.setItemsPerPage();
    }

