export default class Pagination<T>{

    public currentPage: number = 1;
    public totalPages: number;
    public itemsPerPage: number;
    public totalItems: number;
    public page: T[] = [];
    public pageButtons:(number | string)[] = [];
    private items: T[];

    constructor(itemsPerPage:number, items:T[]){
        this.items = items;
        this.itemsPerPage = itemsPerPage;
        this.setTotamItems();
        this.setItemsPerPage();
    }

    setTotamItems(){
        this.totalItems = this.items.length;
    }

    setItemsPerPage(){
        this.totalPages = Math.floor(this.totalItems / this.itemsPerPage);
    }

    updatePageButtons(pageNumber: number, totalItems: number){
        this.pageButtons = [];
        this.pageButtons.push(1);
        if(pageNumber + 4 > totalItems){
                for(let i = pageNumber; i < totalItems; i++){
                    this.pageButtons.push(i);
                }
            } else if(pageNumber + 4 < totalItems && Math.floor(totalItems > (pageNumber + 2))){
                for(let i = pageNumber; i <= pageNumber + 4; i++){
                    if(i === pageNumber + 4){
                        this.pageButtons.push("...");
                    } else {
                        this.pageButtons.push(i);
                    }
                    
                }
            }
        let lastButton = totalItems;
    }

    getPage(pageNumber: number){
        let firstIndex = (this.itemsPerPage * pageNumber) -1;
        let lastIndex = this.totalItems < firstIndex + this.itemsPerPage ? this.totalItems : firstIndex + this.itemsPerPage;
        this.page = [];

        for(let i = firstIndex; i <= lastIndex; i++){
            this.page.push(this.items[i]);
        }
    }

}
