import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-morador',
  standalone: true,
  imports: [ CommonModule, ReactiveFormsModule ],
  templateUrl: './morador.component.html',
  styleUrls: ['./morador.component.css']
})
export default class MoradorComponent implements OnInit {

  formCadastroMorador: FormGroup;

  constructor(
    private fb: FormBuilder
  ){

  }

  ngOnInit(): void {
    this.createFormCadastroMorador();
  }

  createFormCadastroMorador(){
    this.formCadastroMorador = this.fb.group({
      idMorador: ['' , Validators],
      nome: '',
      dataNascimento: '',
      rg: '',
      cpf: '',
      cep: '',
      endereco: '',
      tel: '',
      cel: ''
    });
  }

}
<div class="shadow-lg bg-white w-full h-full rounded-md py-4 px-6">
    <h5 class="mb-4">Morador</h5>
    <form [formGroup]="formCadastroMorador" class="grid grid-cols-8 gap-4">
        <div class="col-span-4">
            <label class="block text-sm font-medium leading-6 text-gray-900 mb-1" for="grid-first-name">
              Nome
            </label>
            <input formControlName="nome" class="primary-form-input" type="text" placeholder="Nome do morador">
        </div>
    </form>
</div>
