import { faHome,
         faEdit,
         faHandPointer,
         faGear,
         faPerson,
         faFileLines,
         faUser,
         IconDefinition } from '@fortawesome/free-solid-svg-icons';
export interface NavigationItem {
    id: string;
    title: string;
    icon: IconDefinition;
    url?: string;
    active?: boolean;
    itens: SideNavItem[]
  }

export interface SideNavItem {
    id: string;
    title: string;
    icon: IconDefinition;
    url?: string;
    hasChild: boolean;
    subItens: SideNavSubItem[]
}

export interface SideNavSubItem {
    id: string;
    title: string;
    url?: string; 
}
  
export class NavigationMenu {

    faHome: IconDefinition = faHome;
    faEdit: IconDefinition = faEdit;
    faHandPointer: IconDefinition = faHandPointer;
    faGear: IconDefinition = faGear;
    faPerson: IconDefinition = faPerson;
    faFileLines: IconDefinition = faFileLines;
    faUser: IconDefinition = faUser;

  private navigationItems: NavigationItem[] = [
    {
        id: 'nav-home',
        title: 'Home',
        url: '/home',
        icon: faHome,
        itens:[
            {
                id: 'nav-pessoa',
                title: 'Pessoa',
                url: '/pessoa',
                icon: faUser,
                hasChild: true,
                subItens:[
                    {
                        id: 'morador',
                        title: 'Morador',
                        url: 'a' 
                    }
                ]
            },
        ]
    },
    {
        id: 'nav-cadastro',
        title: 'Cadastro',
        url: '/cadastro',
        icon: faEdit,
        itens:[]
    },
    {
        id: 'nav-acoes',
        title: 'Ações',
        url: '/acoes',
        icon: faHandPointer,
        itens:[]
    },
    {
        id: 'nav-controladora',
        title: 'Controladora',
        url: '/controladora',
        icon: faGear,
        itens:[]
    },
    {
        id: 'nav-visita',
        title: 'Visita',
        url: '/visita',
        icon: faPerson,
        itens:[]
    },
    {
        id: 'nav-relatorios',
        title: 'Relatórios',
        active: false,
        url: '/relatorios',
        icon: faFileLines,
        itens:[]
    }
    ];

    constructor(){

    }  

    getNavigationMenu(): NavigationItem[]{
        return this.navigationItems;
    }
    
}
